    answer_match = re.search(r'Answer:\s*.*?Passage:\s*(.*?)(?=\n==|$)', problem_text, re.DOTALL)
    if answer_match:
        return answer_match.group(1).strip()

    # If no Answer section found, look for the last occurrence of Passage
    all_passages = re.findall(r'Passage:\s*(.*?)(?=\",\)\n--|$)', problem_text, re.DOTALL)
    if all_passages:
        return all_passages[-1].strip()

    return ""

