def can_reach_24(numbers: List[float], target: float = 24.0, epsilon: float = 1e-10) -> bool:
    if len(numbers) == 1:
        return abs(numbers[0] - target) < epsilon

    for i in range(len(numbers)):
        for j in range(i + 1, len(numbers)):
            a, b = numbers[i], numbers[j]
            remaining = numbers[:i] + numbers[i+1:j] + numbers[j+1:]

            # Try all possible operations
            if can_reach_24(remaining + [a + b], target):
                return True
            if can_reach_24(remaining + [a * b], target):
                return True
            if can_reach_24(remaining + [a - b], target):
                return True
            if can_reach_24(remaining + [b - a], target):
                return True
            if abs(b) > epsilon and can_reach_24(remaining + [a / b], target):
                return True
            if abs(a) > epsilon and can_reach_24(remaining + [b / a], target):
                return True

    return False

def parse_tot_sample(sample: str) -> List[Tuple[str, str]]:
    """
    Parse a single ToT sample and extract new_ys and choices at each step.
    """
    new_ys = re.findall(r"-- new_ys --: \((.*?)\)", sample, re.DOTALL)
    choices = re.findall(r"-- choices --: \[(.*?)\]", sample, re.DOTALL)
    new_ys_steps = [tuple(re.findall(r"'(.*?)'", step)) for step in new_ys]
    return new_ys_steps

def analyze_tot_samples(samples: str, total_steps: int = 4) -> List[float]:
    """
    Analyze ToT samples and compute failure proportions at each step.
    """
    failures = [0] * (total_steps + 1)  # Steps 1 to N, plus a success count
    total_samples = 0

    for sample in samples.strip().split("\n\n"):
        if not sample.strip():
            continue

        total_samples += 1
        steps = parse_tot_sample(sample)

        failed = False
        for step_idx, step_options in enumerate(steps):
            valid_option_exists = any(
                can_reach_24([float(num) for num in re.findall(r"\d+\.?\d*", option.split("(left:")[1])])
                for option in step_options
            )
            if not valid_option_exists:
                failures[step_idx] += 1
                failed = True
                break

        if not failed:
            failures[-1] += 1  # Success count

    # Normalize failures to proportions
    if total_samples > 0:
        failures = [count / total_samples for count in failures]

    return failures

